import metagenerator

#!fmt: off
declarePeripheral(peripheralName = MPU, baseAddress = 0xE000ED90'u32, peripheralDesc = "Memory Protection Unit")
declareRegister(peripheralName = MPU, registerName = MPU_CTRL, addressOffset = 0x4'u32, readAccess = true, writeAccess = true, registerDesc = "Enables the MPU and, when the MPU is enabled, controls whether the default memory map is enabled as a background region for privileged accesses, and whether the MPU is enabled for HardFaults")
declareField(peripheralName = MPU, registerName = MPU_CTRL, fieldName = PRIVDEFENA, bitOffset = 2, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Privileged default enable")
