declareRegister(peripheralName = MPU, registerName = MPU_MAIR1, addressOffset = 0x34'u32, readAccess = true, writeAccess = true, registerDesc = "Along with MPU_MAIR0, provides the memory attribute encodings corresponding to the AttrIndx values")
declareField(peripheralName = MPU, registerName = MPU_MAIR1, fieldName = Attrm, bitOffset = 0, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Attribute m. Memory attribute encoding for MPU regions with an AttrIndx of m")
declareRegister(peripheralName = MPU, registerName = MPU_RBAR, addressOffset = 0xC'u32, readAccess = true, writeAccess = true, registerDesc = "Provides indirect read and write access to the base address of the currently selected MPU region for the selected Security state")
declareField(peripheralName = MPU, registerName = MPU_RBAR, fieldName = BASE, bitOffset = 5, bitWidth = 27, readAccess = true, writeAccess = true, fieldDesc = "Base address. Contains bits [31:5] of the lower inclusive limit of the selected MPU memory region")
declareField(peripheralName = MPU, registerName = MPU_RBAR, fieldName = SH, bitOffset = 3, bitWidth = 2, readAccess = true, writeAccess = true, fieldDesc = "Shareability. Defines the Shareability domain of this region for Normal memory")
declareField(peripheralName = MPU, registerName = MPU_RBAR, fieldName = AP[2:1], bitOffset = 1, bitWidth = 2, readAccess = true, writeAccess = true, fieldDesc = "Access permissions. Defines the access permissions for this region")
declareField(peripheralName = MPU, registerName = MPU_RBAR, fieldName = XN, bitOffset = 0, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Execute Never. Defines whether code can be executed from this region")
declareDimRegister(peripheralName = MPU, registerName = MPU_RBAR_A, addressOffset = 0x00000014'u32, dim = 3, dimIncrement = 8, readAccess = true, writeAccess = true, registerDesc = "Provides indirect read and write access to the base address of the MPU region selected by MPU_RNR[7:2]:(n[1:0]) for the selected Security state")
declareField(peripheralName = MPU, registerName = MPU_RBAR_A, fieldName = BASE, bitOffset = 5, bitWidth = 27, readAccess = true, writeAccess = true, fieldDesc = "Base address. Contains bits [31:5] of the lower inclusive limit of the selected MPU memory region")
declareField(peripheralName = MPU, registerName = MPU_RBAR_A, fieldName = SH, bitOffset = 3, bitWidth = 2, readAccess = true, writeAccess = true, fieldDesc = "Shareability. Defines the Shareability domain of this region for Normal memory")
declareField(peripheralName = MPU, registerName = MPU_RBAR_A, fieldName = AP[2:1], bitOffset = 1, bitWidth = 2, readAccess = true, writeAccess = true, fieldDesc = "Access permissions. Defines the access permissions for this region")
declareField(peripheralName = MPU, registerName = MPU_RBAR_A, fieldName = XN, bitOffset = 0, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Execute Never. Defines whether code can be executed from this region")
declareRegister(peripheralName = MPU, registerName = MPU_RLAR, addressOffset = 0x10'u32, readAccess = true, writeAccess = true, registerDesc = "Provides indirect read and write access to the limit address of the currently selected MPU region for the selected Security state")
declareField(peripheralName = MPU, registerName = MPU_RLAR, fieldName = LIMIT, bitOffset = 5, bitWidth = 27, readAccess = true, writeAccess = true, fieldDesc = "Limit address. Contains bits [31:5] of the upper inclusive limit of the selected MPU memory region")
declareField(peripheralName = MPU, registerName = MPU_RLAR, fieldName = AttrIndx, bitOffset = 1, bitWidth = 3, readAccess = true, writeAccess = true, fieldDesc = "Attribute index. Associates a set of attributes in the MPU_MAIR0 and MPU_MAIR1 fields")
declareField(peripheralName = MPU, registerName = MPU_RLAR, fieldName = EN, bitOffset = 0, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enable. Region enable")
declareDimRegister(peripheralName = MPU, registerName = MPU_RLAR_A, addressOffset = 0x00000018'u32, dim = 3, dimIncrement = 8, readAccess = true, writeAccess = true, registerDesc = "Provides indirect read and write access to the limit address of the currently selected MPU region selected by MPU_RNR[7:2]:(n[1:0]) for the selected Security state")
declareField(peripheralName = MPU, registerName = MPU_RLAR_A, fieldName = LIMIT, bitOffset = 5, bitWidth = 27, readAccess = true, writeAccess = true, fieldDesc = "Limit address. Contains bits [31:5] of the upper inclusive limit of the selected MPU memory region")
declareField(peripheralName = MPU, registerName = MPU_RLAR_A, fieldName = AttrIndx, bitOffset = 1, bitWidth = 3, readAccess = true, writeAccess = true, fieldDesc = "Attribute index. Associates a set of attributes in the MPU_MAIR0 and MPU_MAIR1 fields")
declareField(peripheralName = MPU, registerName = MPU_RLAR_A, fieldName = EN, bitOffset = 0, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enable. Region enable")
declareRegister(peripheralName = MPU, registerName = MPU_RNR, addressOffset = 0x8'u32, readAccess = true, writeAccess = true, registerDesc = "Selects the region currently accessed by MPU_RBAR and MPU_RLAR")
declareField(peripheralName = MPU, registerName = MPU_RNR, fieldName = REGION, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = true, fieldDesc = "Region number. Indicates the memory region accessed by MPU_RBAR and MPU_RLAR")
declareRegister(peripheralName = MPU, registerName = MPU_TYPE, addressOffset = 0x0'u32, readAccess = true, writeAccess = false, registerDesc = "The MPU Type Register indicates how many regions the MPU for the selected Security state supports")
