import metagenerator

#!fmt: off
declarePeripheral(peripheralName = DWT, baseAddress = 0xE0001000'u32, peripheralDesc = "Data Watchpoint and Trace")
declareRegister(peripheralName = DWT, registerName = DWT_CTRL, addressOffset = 0x0'u32, readAccess = true, writeAccess = true, registerDesc = "Data Watchpoint Trace Control Register provides configuration and status information and control of the unit")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = NUMCOMP, bitOffset = 28, bitWidth = 4, readAccess = true, writeAccess = true, fieldDesc = "Number of comparators implemented")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = NOTRCPKT, bitOffset = 27, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Shows whether the implementation supports trace sampling and exception tracing")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = NOEXTTRIG, bitOffset = 26, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Shows whether the implementation includes external match signals, CMPMATCH[N]")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = NOCYCCNT, bitOffset = 25, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Shows whether the implementation supports a cycle counter")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = NOPRFCNT, bitOffset = 24, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Shows whether the implementation supports the profiling counters")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = CYCEVTENA, bitOffset = 22, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables POSTCNT underflow Event counter packets generation")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = FOLDEVTENA, bitOffset = 21, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables generation of the Folded-instruction counter overflow event")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = LSUEVTENA, bitOffset = 20, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables generation of the LSU counter overflow event")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = SLEEPEVTENA, bitOffset = 19, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables generation of the Sleep counter overflow event")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = EXCEVTENA, bitOffset = 18, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables generation of the Exception overhead counter overflow event")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = CPIEVTENA, bitOffset = 17, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables generation of the CPI counter overflow event")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = EXCTRCENA, bitOffset = 16, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables generation of exception trace")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = PCSAMPLENA, bitOffset = 12, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables use of POSTCNT counter as a timer for Periodic PC sample packet generation")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = SYNCTAP, bitOffset = 10, bitWidth = 2, readAccess = true, writeAccess = true, fieldDesc = "Selects the position of the synchronization packet counter tap on the CYCCNT counter")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = CYCTAP, bitOffset = 9, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Selects the position of the POSTCNT tap on the CYCCNT counter")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = POSTINIT, bitOffset = 5, bitWidth = 4, readAccess = true, writeAccess = true, fieldDesc = "Initial value for the POSTCNT counter")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = POSTPRESET, bitOffset = 1, bitWidth = 4, readAccess = true, writeAccess = true, fieldDesc = "Reload value for the POSTCNT counter")
declareField(peripheralName = DWT, registerName = DWT_CTRL, fieldName = CYCCNTENA, bitOffset = 0, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables CYCCNT")
declareRegister(peripheralName = DWT, registerName = DWT_CYCCNT, addressOffset = 0x4'u32, readAccess = true, writeAccess = true, registerDesc = "Cycle Count register shows or sets the value of the processor cycle counter, CYCCNT")
declareField(peripheralName = DWT, registerName = DWT_CYCCNT, fieldName = CYCCNT, bitOffset = 0, bitWidth = 32, readAccess = true, writeAccess = true, fieldDesc = "Incrementing cycle counter value")
declareRegister(peripheralName = DWT, registerName = DWT_CPICNT, addressOffset = 0x8'u32, readAccess = true, writeAccess = true, registerDesc = "CPI Count register counts additional cycles required to execute multi-cycle instructions and instruction fetch stalls")
declareField(peripheralName = DWT, registerName = DWT_CPICNT, fieldName = CPICNT, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = true, fieldDesc = "Base instruction overhead counter")
declareRegister(peripheralName = DWT, registerName = DWT_EXCCNT, addressOffset = 0xC'u32, readAccess = true, writeAccess = true, registerDesc = "Exception Overhead Count register counts the total cycles spent in exception processing")
declareField(peripheralName = DWT, registerName = DWT_EXCCNT, fieldName = EXCCNT, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = true, fieldDesc = "The exception overhead counter")
declareRegister(peripheralName = DWT, registerName = DWT_SLEEPCNT, addressOffset = 0x10'u32, readAccess = true, writeAccess = true, registerDesc = "Sleep Count register counts the total number of cycles that the processor is sleeping")
declareField(peripheralName = DWT, registerName = DWT_SLEEPCNT, fieldName = SLEEPCNT, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = true, fieldDesc = "Sleep counter")
declareRegister(peripheralName = DWT, registerName = DWT_LSUCNT, addressOffset = 0x14'u32, readAccess = true, writeAccess = true, registerDesc = "LSU Count register increments on the additional cycles required to execute all load or store instructions")
declareField(peripheralName = DWT, registerName = DWT_LSUCNT, fieldName = LSUCNT, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = true, fieldDesc = "Load-store overhead counter")
declareRegister(peripheralName = DWT, registerName = DWT_FOLDCNT, addressOffset = 0x18'u32, readAccess = true, writeAccess = true, registerDesc = "Folded-instruction Count register increments on each instruction that takes 0 cycles")
declareField(peripheralName = DWT, registerName = DWT_FOLDCNT, fieldName = FOLDCNT, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = true, fieldDesc = "Folded instruction counter")
declareRegister(peripheralName = DWT, registerName = DWT_PCSR, addressOffset = 0x1C'u32, readAccess = true, writeAccess = false, registerDesc = "Program Counter Sample Register samples the current value of the program counter")
declareField(peripheralName = DWT, registerName = DWT_PCSR, fieldName = EIASAMPLE, bitOffset = 0, bitWidth = 32, readAccess = true, writeAccess = false, fieldDesc = "Executed Instruction Address sample value")
declareDimRegister(peripheralName = DWT, registerName = DWT_COMP, addressOffset = 0x00000020'u32, dim = 16, dimIncrement = 16, readAccess = true, writeAccess = true, registerDesc = "Comparator register provides a reference value for use by watchpoint comparator n")
declareField(peripheralName = DWT, registerName = DWT_COMP, fieldName = COMP, bitOffset = 0, bitWidth = 32, readAccess = true, writeAccess = true, fieldDesc = "Reference value for comparison")
declareDimRegister(peripheralName = DWT, registerName = DWT_MASK, addressOffset = 0x00000024'u32, dim = 16, dimIncrement = 16, readAccess = true, writeAccess = true, registerDesc = "Comparator Mask register provides the size of the ignore mask applied to the access address for address range matching by comparator n")
declareField(peripheralName = DWT, registerName = DWT_MASK, fieldName = MASK, bitOffset = 0, bitWidth = 5, readAccess = true, writeAccess = true, fieldDesc = "The size of the ignore mask, 0-31 bits, applied to address range matching")
declareDimRegister(peripheralName = DWT, registerName = DWT_FUNCTION, addressOffset = 0x00000028'u32, dim = 16, dimIncrement = 16, readAccess = true, writeAccess = true, registerDesc = "Data Watchpoint Trace Comparator Function Register")
declareField(peripheralName = DWT, registerName = DWT_FUNCTION, fieldName = MATCHED, bitOffset = 24, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Comparator match")
declareField(peripheralName = DWT, registerName = DWT_FUNCTION, fieldName = DATAVADDR1, bitOffset = 16, bitWidth = 4, readAccess = true, writeAccess = true, fieldDesc = "When the DATAVMATCH and LNK1ENA bits are both 1, this field can hold the comparator number of a second comparator to use for linked address comparison")
declareField(peripheralName = DWT, registerName = DWT_FUNCTION, fieldName = DATAVADDR0, bitOffset = 12, bitWidth = 4, readAccess = true, writeAccess = true, fieldDesc = "When the DATAVMATCH bit is set to 1 this field can hold the comparator number of a comparator to use for linked address comparison")
declareField(peripheralName = DWT, registerName = DWT_FUNCTION, fieldName = DATAVSIZE, bitOffset = 10, bitWidth = 2, readAccess = true, writeAccess = true, fieldDesc = "For data value matching, specifies the size of the required data comparison")
declareField(peripheralName = DWT, registerName = DWT_FUNCTION, fieldName = LNK1ENA, bitOffset = 9, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Indicates whether the implementation supports use of a second linked comparator")
declareField(peripheralName = DWT, registerName = DWT_FUNCTION, fieldName = DATAVMATCH, bitOffset = 8, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables data value comparison, if supported")
declareField(peripheralName = DWT, registerName = DWT_FUNCTION, fieldName = CYCMATCH, bitOffset = 7, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "DWT_FUNCTION0 only")
declareField(peripheralName = DWT, registerName = DWT_FUNCTION, fieldName = EMITRANGE, bitOffset = 5, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "If the implementation supports trace sampling, enables generation of Data trace address packets, that hold Daddr[15")
declareField(peripheralName = DWT, registerName = DWT_FUNCTION, fieldName = FUNCTION, bitOffset = 0, bitWidth = 4, readAccess = true, writeAccess = true, fieldDesc = "Selects action taken on comparator match")
