import metagenerator

#!fmt: off
declarePeripheral(peripheralName = FP, baseAddress = 0xE000EF34'u32, peripheralDesc = "Floating-Point Extension")
declareRegister(peripheralName = FP, registerName = FPCCR, addressOffset = 0x0'u32, readAccess = true, writeAccess = true, registerDesc = "Floating Point Context Control Register holds control data for the Floating Point Unit")
declareField(peripheralName = FP, registerName = FPCCR, fieldName = ASPEN, bitOffset = 31, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "When this bit is set to 1, execution of a floating-point instruction sets the CONTROL")
declareField(peripheralName = FP, registerName = FPCCR, fieldName = LSPEN, bitOffset = 30, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Enables lazy context save of FP state")
declareField(peripheralName = FP, registerName = FPCCR, fieldName = MONRDY, bitOffset = 8, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Indicates whether the software executing when the processor allocated the FP stack frame was able to set the DebugMonitor exception to pending")
declareField(peripheralName = FP, registerName = FPCCR, fieldName = BFRDY, bitOffset = 6, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Indicates whether the software executing when the processor allocated the FP stack frame was able to set the BusFault exception to pending")
declareField(peripheralName = FP, registerName = FPCCR, fieldName = MMRDY, bitOffset = 5, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Indicates whether the software executing when the processor allocated the FP stack frame was able to set the MemManage exception to pending")
declareField(peripheralName = FP, registerName = FPCCR, fieldName = HFRDY, bitOffset = 4, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Indicates whether the software executing when the processor allocated the FP stack frame was able to set the HardFault exception to pending")
declareField(peripheralName = FP, registerName = FPCCR, fieldName = THREAD, bitOffset = 3, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Indicates the processor mode when it allocated the FP stack frame")
declareField(peripheralName = FP, registerName = FPCCR, fieldName = USER, bitOffset = 1, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Indicates the privilege level of the software executing when the processor allocated the FP stack frame")
declareField(peripheralName = FP, registerName = FPCCR, fieldName = LSPACT, bitOffset = 0, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Indicates whether Lazy preservation of the FP state is active")
declareRegister(peripheralName = FP, registerName = FPCAR, addressOffset = 0x4'u32, readAccess = true, writeAccess = true, registerDesc = "Floating Point Context Address Register holds the location of the unpopulated floating-point register space allocated on an exception stack frame")
declareRegister(peripheralName = FP, registerName = FPDSCR, addressOffset = 0x8'u32, readAccess = true, writeAccess = true, registerDesc = "Floating Point Default Status Control Register holds the default values for the floating-point status control data that the processor assigns to the FPSCR when it creates a new floating-point context")
declareField(peripheralName = FP, registerName = FPDSCR, fieldName = AHP, bitOffset = 26, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Default value for FPSCR.AHP")
declareField(peripheralName = FP, registerName = FPDSCR, fieldName = DN, bitOffset = 25, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Default value for FPSCR.DN")
declareField(peripheralName = FP, registerName = FPDSCR, fieldName = FZ, bitOffset = 24, bitWidth = 1, readAccess = true, writeAccess = true, fieldDesc = "Default value for FPSCR.FZ")
declareField(peripheralName = FP, registerName = FPDSCR, fieldName = RMode, bitOffset = 22, bitWidth = 2, readAccess = true, writeAccess = true, fieldDesc = "Default value for FPSCR.RMode")
declareRegister(peripheralName = FP, registerName = MVFR0, addressOffset = 0xC'u32, readAccess = true, writeAccess = false, registerDesc = "Media and FP Feature Register 0 describes the features provided by the Floating-point extension")
declareField(peripheralName = FP, registerName = MVFR0, fieldName = FP_rounding_modes, bitOffset = 28, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates the rounding modes supported by the FP floating-point hardware")
declareField(peripheralName = FP, registerName = MVFR0, fieldName = Short_vectors, bitOffset = 24, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates the hardware support for FP short vectors")
declareField(peripheralName = FP, registerName = MVFR0, fieldName = Square_root, bitOffset = 20, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates the hardware support for FP square root operations")
declareField(peripheralName = FP, registerName = MVFR0, fieldName = Divide, bitOffset = 16, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates the hardware support for FP divide operations")
declareField(peripheralName = FP, registerName = MVFR0, fieldName = FP_exception_trapping, bitOffset = 12, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates whether the FP hardware implementation supports exception trapping")
declareField(peripheralName = FP, registerName = MVFR0, fieldName = Double_precision, bitOffset = 8, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates the hardware support for FP double-precision operations")
declareField(peripheralName = FP, registerName = MVFR0, fieldName = Single_precision, bitOffset = 4, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates the hardware support for FP single-precision operations")
declareField(peripheralName = FP, registerName = MVFR0, fieldName = A_SIMD_registers, bitOffset = 0, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates the size of the FP register bank")
declareRegister(peripheralName = FP, registerName = MVFR1, addressOffset = 0x10'u32, readAccess = true, writeAccess = false, registerDesc = "Media and FP Feature Register 1 describes the features provided by the Floating-point extension")
declareField(peripheralName = FP, registerName = MVFR1, fieldName = FP_fused_MAC, bitOffset = 28, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates whether the FP supports fused multiply accumulate operations")
declareField(peripheralName = FP, registerName = MVFR1, fieldName = FP_HPFP, bitOffset = 24, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Floating Point Half-Precision and double-precision")
declareField(peripheralName = FP, registerName = MVFR1, fieldName = D_NaN_mode, bitOffset = 4, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates whether the FP hardware implementation supports only the Default NaN mode")
declareField(peripheralName = FP, registerName = MVFR1, fieldName = FtZ_mode, bitOffset = 0, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates whether the FP hardware implementation supports only the Flush-to-Zero mode of operation")
declareRegister(peripheralName = FP, registerName = MVFR2, addressOffset = 0x14'u32, readAccess = true, writeAccess = false, registerDesc = "Media and FP Feature Register 2 describes the features provided by the floating-point extension")
declareField(peripheralName = FP, registerName = MVFR2, fieldName = VFP_Misc, bitOffset = 4, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Indicates the hardware support for FP miscellaneous features")
