# To learn how to use the output of the declarations below, visit:
# https://github.com/dwhall/minisvd2nim/blob/main/README.md#how-to-access-the-device

import metagenerator

#!fmt: off
declarePeripheral(peripheralName = EWIC CoreSight, baseAddress = 0xE0047F00'u32, peripheralDesc = "External Wakeup Interrupt Controller CoreSight registers")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_ITCTRL, addressOffset = 0x0'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Integration Mode Control Register used to dynamically switch between functional mode and integration mode. The EWIC does not support integration mode, and this register is RAZ.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_CLAIMSET, addressOffset = 0xA0'u32, readAccess = true, writeAccess = true, registerDesc = "EWIC Claim Tag Set Register the EWIC_CLAIMSET register is used to set whether functionality is in use by a debug agent. The EWIC does not have any associated debug functionality, and this register is 0xF.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_CLAIMCLR, addressOffset = 0xA4'u32, readAccess = true, writeAccess = true, registerDesc = "EWIC Claim Tag Clear Register the EWIC_CLAIMCLR register is used to set whether functionality is in use by a debug agent. The EWIC does not have any associated debug functionality, and this register is 0x0.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_DEVAFF0, addressOffset = 0xA8'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Device Affinity Register 0 the EWIC_DEVAFF0 register enables a debugger to determine whether the EWIC and the processor have an affinity with each other. The EWIC does not have any associated debug functionality, and this register is 0x80000000.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_DEVAFF1, addressOffset = 0xAC'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Device Affinity Register 1 the EWIC_DEVAFF1 register enables a debugger to determine whether the EWIC and the processor have an affinity with each other. The EWIC does not have any associated debug functionality, and this register is 0x0.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_LAR, addressOffset = 0xB0'u32, readAccess = false, writeAccess = true, registerDesc = "EWIC Software Lock Access Register the EWIC_LAR register controls software access to CoreSight components to reduce the likelihood of accidental access to the EWIC. The EWIC does not support software locking, and writing to this register has no affect.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_LSR, addressOffset = 0xB4'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Software Lock Status Register the EWIC_LSR register controls software access to CoreSight components to reduce the likelihood of accidental access to the EWIC. The EWIC does not support software locking, and this register is RAZ.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_AUTHSTATUS, addressOffset = 0xB8'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Authentication Status Register the EWIC_AUTHSTATUS register indicates whether the EWIC includes debug functionality. The EWIC is not a debug component, therefore, the EWIC_AUTHSTATIS register is RAZ.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_DEVARCH, addressOffset = 0xBC'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Device Architecture Register identifies the architecture of the EWIC")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_DEVARCH, fieldName = ARCHITECT_JEP106C, bitOffset = 28, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Defines the architect of the component, JEP106 continuation code")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_DEVARCH, fieldName = ARCHITECT_JEP106I, bitOffset = 21, bitWidth = 7, readAccess = true, writeAccess = false, fieldDesc = "Defines the architect of the component, JEP106 identification code")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_DEVARCH, fieldName = PRESENT, bitOffset = 20, bitWidth = 1, readAccess = true, writeAccess = false, fieldDesc = "Indicates the presence of this register. This value is 0b1.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_DEVARCH, fieldName = REVISION, bitOffset = 16, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Architecture revision. This value is 0b0000.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_DEVARCH, fieldName = ARCHID, bitOffset = 0, bitWidth = 16, readAccess = true, writeAccess = false, fieldDesc = "Returns a value that identifies the architecture of the component. This value is 0x0A07 corresponding to EWIC architecture.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_DEVID2, addressOffset = 0xC0'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Device Configuration Register 2 the EWIC_DEVID2 indicates some capabilities of the EWIC. This register is RAZ/WI.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_DEVID1, addressOffset = 0xC4'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Device Configuration Register 1 the EWIC_DEVID1 indicates some capabilities of the EWIC. This register is RAZ/WI.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_DEVID, addressOffset = 0xC8'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Device Configuration Register the EWIC_DEVID indicates some capabilities of the EWIC. This register is RAZ/WI.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_DEVTYPE, addressOffset = 0xCC'u32, readAccess = true, writeAccess = false, registerDesc = "EWIC Device Type Identifier Register, EWIC_DEVTYPE provides part number information about the EWIC component to the debugger.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_DEVTYPE, fieldName = SUB, bitOffset = 4, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Sub type for the component device type. This field is set to 0b0000.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_DEVTYPE, fieldName = MAJOR, bitOffset = 0, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Major type for the component device type. This field is set to 0b0000.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR4, addressOffset = 0xD0'u32, readAccess = true, writeAccess = false, registerDesc = "Peripheral Identification Register 4 provides information about the memory size and JEP106 continuation code that the External Wakeup Interrupt Controller (EWIC) component uses")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR4, fieldName = SIZE, bitOffset = 4, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "This field indicates the memory size that the EWIC uses. This field returns 0x0 indicating that the component uses an UNKNOWN number of 4KB blocks.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR4, fieldName = DES_2, bitOffset = 0, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "JEP106 continuation code. Together with EWIC_PIDR2.DES_1 and EWIC_PIDR1.DES_0, they indicate the designer of the component, not the implementer, except where the two are the same. The reset value of this field is 0x4.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR5, addressOffset = 0xD4'u32, readAccess = true, writeAccess = false, registerDesc = "Peripheral Identification Register 5 the register is reserved, RES0")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR6, addressOffset = 0xD8'u32, readAccess = true, writeAccess = false, registerDesc = "Peripheral Identification Register 6 the register is reserved, RES0")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR7, addressOffset = 0xDC'u32, readAccess = true, writeAccess = false, registerDesc = "Peripheral Identification Register 7 the register is reserved, RES0")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR0, addressOffset = 0xE0'u32, readAccess = true, writeAccess = false, registerDesc = "Peripheral Identification Register 0 indicates the EWIC component part number")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR0, fieldName = PART_0, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = false, fieldDesc = "This field indicates the part number. When taken together with EWIC_PIDR1.PART_1, it indicates the component. The part number is selected by the designer of the component.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR1, addressOffset = 0xE4'u32, readAccess = true, writeAccess = false, registerDesc = "Peripheral Identification Register 1 indicates the EWIC component JEP106 continuation code and part number")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR1, fieldName = DES_0, bitOffset = 4, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "This field indicates the JEP106 identification code, bits[3:0]. Together, with EWIC_PIDR4.DES_2 and EWIC_PIDR2.DES_1, they indicate the designer of the component and not the implementer, except where the two are the same. The reset value is 0xB.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR1, fieldName = PART_1, bitOffset = 0, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "This field indicates the part number, bits[11:8]. Taken together with EWIC_PIDR0.PART_0 it indicates the component. The part number is selected by the designer of the component. The reset value is 0xD.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR2, addressOffset = 0xE8'u32, readAccess = true, writeAccess = false, registerDesc = "Indicates the EWIC component revision number, JEDEC value, and part of the JEP106 continuation code")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR2, fieldName = REVISION, bitOffset = 4, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "This field indicates the revision number of the EWIC component. It is an incremental value starting at 0x0 for the first design. The reset value is 0x0.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR2, fieldName = JEDEC, bitOffset = 3, bitWidth = 1, readAccess = true, writeAccess = false, fieldDesc = "This field is always 1, indicating that a JEDEC assigned value is used.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR2, fieldName = DES_1, bitOffset = 0, bitWidth = 3, readAccess = true, writeAccess = false, fieldDesc = "This field is the JEP106 identification code, bits[6:4]. Together, with CTI_PIDR4.DES_2 and CTI_PIDR1.DES_0, they indicate the designer of the component and not the implementer, except where the two are the same. The reset value is 0b011.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR3, addressOffset = 0xEC'u32, readAccess = true, writeAccess = false, registerDesc = "Indicates minor errata fixes of the Cross Trigger Interface (CTI) component and if you have modified the behavior of the component")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR3, fieldName = REVAND, bitOffset = 4, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "This field indicates minor errata fixes specific to this design, for example metal fixes after implementation. In most cases this field is 0x0.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_PIDR3, fieldName = CMOD, bitOffset = 0, bitWidth = 4, readAccess = true, writeAccess = false, fieldDesc = "Customer modified. Where the component is reusable IP, this value indicates whether you have modified the behavior of the component. In most cases, this field is 0x0.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_CIDR0, addressOffset = 0xF0'u32, readAccess = true, writeAccess = false, registerDesc = "Component Identification Register 0 indicates the preamble")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_CIDR0, fieldName = PRMBL_0, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = false, fieldDesc = "Preamble. This field returns 0x0D.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_CIDR1, addressOffset = 0xF4'u32, readAccess = true, writeAccess = false, registerDesc = "Component Identification Register 1 indicates the component class and preamble")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_CIDR1, fieldName = CLASS, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = false, fieldDesc = "Component class. Returns 0x9, indicating this is a CoreSight component.")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_CIDR1, fieldName = PRMBL_1, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = false, fieldDesc = "Preamble. This field returns 0x0.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_CIDR2, addressOffset = 0xF8'u32, readAccess = true, writeAccess = false, registerDesc = "Component Identification Register 2 indicates the preamble")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_CIDR2, fieldName = PRMBL_2, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = false, fieldDesc = "Preamble. This field returns 0x05.")
declareRegister(peripheralName = EWIC CoreSight, registerName = EWIC_CIDR3, addressOffset = 0xFC'u32, readAccess = true, writeAccess = false, registerDesc = "Component Identification Register 3 indicates the preamble")
declareField(peripheralName = EWIC CoreSight, registerName = EWIC_CIDR3, fieldName = PRMBL_3, bitOffset = 0, bitWidth = 8, readAccess = true, writeAccess = false, fieldDesc = "Preamble. This field returns 0xB1.")
